<script>import GeneralObserver from "./general-observer.svelte";
export let width = "100%";
export let height = "420px";
export let username = "";
export let title = "";
export let byline = "hidden";
export let share = "hidden";
export let style = "dark";
export let disable_observer = false;
let baseUrl = `https://slides.com/${username}/${title}/embed?`;
const config = {
    style,
    byline,
    share
};
const queryString = new URLSearchParams(config);
const src = `${baseUrl}&${queryString.toString()}`;
</script>

<GeneralObserver {disable_observer}>
	<iframe
		{src}
		{width}
		{height}
		{title}
		scrolling="no"
		frameborder="0"
		allowfullscreen
		style="width: 100%;"
	/>
</GeneralObserver>
